import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const ProductForm = () => {
    const [product, setProduct] = useState({
        nombre: '',
        descripcion: '',
        precio: '',
        stock: ''
    });
    const { id } = useParams();
    const navigate = useNavigate(); // Reemplaza useHistory por useNavigate

    useEffect(() => {
        if (id) {
            fetchProduct(id);
        }
    }, [id]);

    const fetchProduct = async (id) => {
        const response = await axios.get(`/api/productos/${id}`);
        setProduct(response.data.product);
    };

    const handleChange = (e) => {
        setProduct({
            ...product,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (id) {
            await axios.put(`/api/productos/${id}`, product);
        } else {
            await axios.post('/api/productos', product);
        }

        navigate('/productos'); // Reemplaza history.push por navigate
    };

    return (
        <div>
            <h1>{id ? 'Editar Producto' : 'Agregar Producto'}</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre:</label>
                    <input type="text" name="nombre" value={product.nombre} onChange={handleChange} required />
                </div>
                <div>
                    <label>Descripci√≥n:</label>
                    <textarea name="descripcion" value={product.descripcion} onChange={handleChange} required />
                </div>
                <div>
                    <label>Precio:</label>
                    <input type="number" name="precio" value={product.precio} onChange={handleChange} required />
                </div>
                <div>
                    <label>Stock:</label>
                    <input type="number" name="stock" value={product.stock} onChange={handleChange} required />
                </div>
                <button type="submit">{id ? 'Actualizar' : 'Crear'}</button>
            </form>
        </div>
    );
};

export default ProductForm;
