import React, { useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom'; // Reemplaza useHistory por useNavigate

const ProductDelete = () => {
    const { id } = useParams();
    const navigate = useNavigate(); // Reemplaza useHistory por useNavigate

    useEffect(() => {
        deleteProduct();
    }, []);

    const deleteProduct = async () => {
        await axios.delete(`/api/productos/${id}`);
        navigate('/productos'); // Reemplaza history.push por navigate
    };

    return (
        <div>
            <h1>Eliminando Producto...</h1>
        </div>
    );
};

export default ProductDelete;
